import logo from './logo.svg';
import './App.css';

class App extends Component {
  render() {
    return (
      <div>
        {
          1 + 1 === 4 //true 일 때와 false 일 때 다른것들을 보여주고 싶을 떄 사용
            ? (<div>맞아요!</div>)
            : (<div>틀려요!</div>)
        }
        {
          1 + 1 === 2 && (<div>맞아요!</div>) //조건이 true 일 때만 보여주고 false 경우 아무것도 보여주고 싶지 않을 때 사용
        }
      </div>
    );
  }
}

export default App;
